package unidad1;

public class AreaCirculo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float pi = 3.1416f;
		float radio = 10.25f;
		float rcuadrado = radio * radio;
		//Calculamos el area  del radio pi * radio al cuadrado
		float area = pi * rcuadrado;

		System.out.println("El área del círculo es " + area);

	}

}
